library(tidyverse)
library(here)
airstrikes &lt;- read_csv(here("data/DroneWarsData2.csv"))
library(lubridate)
# airstrikes tidy
airstrikes_tidy &lt;- as_tibble(airstrikes) |&gt;
  mutate(Date = mdy(`Date (MM-DD-YYYY)`)) |&gt;
  select(!c(`Date (MM-DD-YYYY)`, 18:36)) |&gt;
  relocate(Date) |&gt;
  mutate(Longitude = as.numeric(Longitude), Latitude = as.numeric(Latitude))

airstrikes_tidy &lt;- airstrikes_tidy[complete.cases(airstrikes_tidy[, 1:17]),]
